import os, json

raw = os.environ.get("SHUFFLE_REQUEST_BODY") or os.environ.get("SHUFFLE_INPUT") or "{}"

try:
    data = json.loads(raw)
except Exception:
    data = {}

verdict = str(data.get("ml_verdict", "")).strip().lower()
vt_raw = data.get("vt_score", 0)

try:
    vt = int(vt_raw)
except Exception:
    try:
        vt = int(float(vt_raw))
    except Exception:
        vt = 0

result = {
    "is_phishing": (verdict == "phishing"),
    "high_risk": (verdict == "phishing" and vt > 0),
    "verdict": verdict,
    "vt_score": vt
}
